@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 20 14% 4%;
    --foreground: 60 9% 98%;

    --card: 20 14% 7%;
    --card-foreground: 60 9% 98%;

    --popover: 20 14% 7%;
    --popover-foreground: 60 9% 98%;

    --primary: 38 92% 50%;
    --primary-foreground: 20 14% 4%;

    --secondary: 20 14% 14%;
    --secondary-foreground: 60 9% 98%;

    --muted: 20 14% 18%;
    --muted-foreground: 20 10% 55%;

    --accent: 38 92% 50%;
    --accent-foreground: 20 14% 4%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 60 9% 98%;

    --success: 142 76% 36%;
    --success-foreground: 60 9% 98%;

    --warning: 38 92% 50%;
    --warning-foreground: 20 14% 4%;

    --border: 20 14% 16%;
    --input: 20 14% 16%;
    --ring: 38 92% 50%;

    --radius: 0.75rem;

    /* Custom tokens */
    --gradient-primary: linear-gradient(135deg, hsl(38 92% 50%), hsl(28 92% 45%));
    --gradient-card: linear-gradient(145deg, hsl(20 14% 9%), hsl(20 14% 6%));
    --gradient-glass: linear-gradient(135deg, hsl(20 14% 12% / 0.6), hsl(20 14% 8% / 0.4));
    --shadow-glow: 0 0 40px hsl(38 92% 50% / 0.15);
    --shadow-card: 0 4px 24px hsl(0 0% 0% / 0.3);
    --shadow-elevated: 0 8px 32px hsl(0 0% 0% / 0.4);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  .glass-card {
    @apply bg-card/60 backdrop-blur-xl border border-border/50 rounded-xl;
    background: var(--gradient-glass);
  }

  .stat-card {
    @apply bg-card border border-border/50 rounded-xl p-6 transition-all duration-300;
    &:hover {
      @apply border-primary/30;
      box-shadow: var(--shadow-glow);
    }
  }

  .glow-text {
    @apply text-primary;
    text-shadow: 0 0 20px hsl(38 92% 50% / 0.5);
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-out forwards;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out forwards;
  }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

