input.content:
export interface Client {
  id: string;
  name: string;
  email: string;
  phone: string;
  avatar?: string;
  referredBy?: string;
  referralCount: number;
  totalSpent: number;
  createdAt: Date;
}

export interface Gallery {
  id: string;
  clientId: string;
  clientName: string;
  title: string;
  coverImage: string;
  imageCount: number;
  status: 'editing' | 'ready' | 'delivered';
  deliveryDate?: Date;
  createdAt: Date;
}

export interface Booking {
  id: string;
  clientId: string;
  clientName: string;
  clientAvatar?: string;
  packageId: string;
  packageName: string;
  date: Date;
  time: string;
  location: string;
  status: 'pending' | 'confirmed' | 'completed' | 'cancelled';
  notes?: string;
  reminder?: Date;
}

export interface PricingPackage {
  id: string;
  name: string;
  price: number;
  description: string;
  features: string[];
  duration: string;
  popular?: boolean;
}

export interface DashboardStats {
  totalClients: number;
  upcomingBookings: number;
  pendingDeliveries: number;
  monthlyRevenue: number;
  referralsThisMonth: number;
}

import {
  LayoutDashboard,
  Users,
  Image,
  Calendar,
  DollarSign,
  UserPlus,
  Camera
} from 'lucide-react';

interface SidebarProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const navItems = [
  { id: 'dashboard', label: 'Dashboard', icon: LayoutDashboard },
  { id: 'clients', label: 'Clients', icon: Users },
  { id: 'galleries', label: 'Galleries', icon: Image },
  { id: 'bookings', label: 'Bookings', icon: Calendar },
  { id: 'packages', label: 'Packages', icon: DollarSign },
  { id: 'referrals', label: 'Referrals', icon: UserPlus },
];

export function Sidebar({ activeTab, onTabChange }: SidebarProps) {
  return (
    <aside className="w-64 bg-sidebar border-r border-sidebar-border h-screen fixed left-0 top-0 flex flex-col">
      <div className="p-6 border-b border-sidebar-border">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-lg bg-primary/20 flex items-center justify-center">
            <Camera className="w-5 h-5 text-primary" />
          </div>
          <div>
            <h1 className="font-semibold text-lg text-sidebar-foreground">PhotoCRM</h1>
            <p className="text-xs text-muted-foreground">Studio Manager</p>
          </div>
        </div>
      </div>

      <nav className="flex-1 p-4 space-y-1">
        {navItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onTabChange(item.id)}
            className={`nav-item w-full ${activeTab === item.id ? 'active' : ''}`}
          >
            <item.icon className="w-5 h-5" />
            <span>{item.label}</span>
          </button>
        ))}
      </nav>

      <div className="p-4 border-t border-sidebar-border">
        <div className="glass-card p-4">
          <p className="text-sm font-medium text-foreground">Need help?</p>
          <p className="text-xs text-muted-foreground mt-1">Check our documentation for tips and best practices.</p>
        </div>
      </div>
    </aside>
  );
}

import { LucideIcon } from 'lucide-react';

interface StatsCardProps {
  title: string;
  value: string | number;
  icon: LucideIcon;
  trend?: {
    value: number;
    isPositive: boolean;
  };
  delay?: number;
}

export function StatsCard({ title, value, icon: Icon, trend, delay = 0 }: StatsCardProps) {
  return (
    <div
      className="stat-card animate-slide-up"
      style={{ animationDelay: `${delay}ms` }}
    >
      <div className="flex items-start justify-between">
        <div>
          <p className="text-sm text-muted-foreground">{title}</p>
          <p className="text-3xl font-bold mt-2 text-foreground">{value}</p>
          {trend && (
            <p className={`text-sm mt-2 ${trend.isPositive ? 'text-success' : 'text-destructive'}`}>
