import { useState } from 'react';
import { Client } from '@/types';
import { Button } from '@/components/ui/button';
import { Plus, Search, Mail, Phone, DollarSign } from 'lucide-react';

interface ClientsViewProps {
  clients: Client[];
}

export function ClientsView({ clients }: ClientsViewProps) {
  const [search, setSearch] = useState('');

  const filteredClients = clients.filter(client =>
    client.name.toLowerCase().includes(search.toLowerCase()) ||
    client.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="animate-fade-in">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Clients</h2>
          <p className="text-muted-foreground mt-1">Manage your client relationships</p>
        </div>
        <Button>
          <Plus className="w-4 h-4" />
          Add Client
        </Button>
      </div>

      <div className="bg-card border border-border/50 rounded-xl">
        <div className="p-4 border-b border-border/50">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <input
              type="text"
              placeholder="Search clients..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-secondary/50 border border-border/50 rounded-lg text-foreground placeholder:text-muted-foreground focus:outline-none focus:border-primary/50"
            />
          </div>
        </div>

        <div className="divide-y divide-border/50">
          {filteredClients.map((client, index) => (
            <div
              key={client.id}
              className="p-4 hover:bg-secondary/30 transition-colors cursor-pointer animate-slide-in"
              style={{ animationDelay: `${index * 50}ms` }}
            >
              <div className="flex items-center gap-4">
                {client.avatar ? (
                  <img
                    src={client.avatar}
                    alt={client.name}
                    className="w-12 h-12 rounded-full object-cover"
                  />
                ) : (
                  <div className="w-12 h-12 rounded-full bg-primary/20 flex items-center justify-center">
                    <span className="text-primary font-semibold text-lg">{client.name[0]}</span>
                  </div>
                )}

                <div className="flex-1 min-w-0">
                  <p className="font-medium text-foreground">{client.name}</p>
                  <div className="flex items-center gap-4 mt-1">
                    <span className="flex items-center gap-1 text-sm text-muted-foreground">
                      <Mail className="w-3.5 h-3.5" />
                      {client.email}
                    </span>
                    <span className="flex items-center gap-1 text-sm text-muted-foreground">
                      <Phone className="w-3.5 h-3.5" />
                      {client.phone}
                    </span>
                  </div>
                </div>

                <div className="text-right">
                  <div className="flex items-center gap-1 text-foreground font-medium">
                    <DollarSign className="w-4 h-4 text-primary" />
                    {client.totalSpent.toLocaleString()}
                  </div>
                  <p className="text-sm text-muted-foreground mt-1">
                    {client.referralCount} referrals
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Gallery } from '@/types';
import { Button } from '@/components/ui/button';
import { Plus, Image, Clock, Check, Send, Download, Eye, MoreHorizontal } from 'lucide-react';

interface GalleriesViewProps {
  galleries: Gallery[];
}

function getStatusIcon(status: Gallery['status']) {
  switch (status) {
    case 'editing': return <Clock className="w-4 h-4" />;
    case 'ready': return <Check className="w-4 h-4" />;
    case 'delivered': return <Send className="w-4 h-4" />;
  }
}

function getStatusClass(status: Gallery['status']) {
  switch (status) {
    case 'editing': return 'status-pending';
    case 'ready': return 'status-confirmed';
    case 'delivered': return 'status-delivered';
  }
}

export function GalleriesView({ galleries }: GalleriesViewProps) {
  const [filter, setFilter] = useState<'all' | Gallery['status']>('all');

  const filteredGalleries = filter === 'all'
    ? galleries
    : galleries.filter(g => g.status === filter);

  return (
    <div className="animate-fade-in">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Galleries</h2>
          <p className="text-muted-foreground mt-1">Manage and deliver client galleries</p>
        </div>
        <Button>
          <Plus className="w-4 h-4" />
          Create Gallery
        </Button>
      </div>

      <div className="flex gap-2 mb-6">
        {(['all', 'editing', 'ready', 'delivered'] as const).map((status) => (
          <button
            key={status}
            onClick={() => setFilter(status)}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              filter === status
                ? 'bg-primary text-primary-foreground'
                : 'bg-secondary text-muted-foreground hover:text-foreground'
            }`}
          >
            {status.charAt(0).toUpperCase() + status.slice(1)}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredGalleries.map((gallery, index) => (
          <div
            key={gallery.id}
            className="bg-card border border-border/50 rounded-xl overflow-hidden group animate-slide-up"
            style={{ animationDelay: `${index * 100}ms` }}
          >
            <div className="relative aspect-[16/10] overflow-hidden">
              <img
                src={gallery.coverImage}
                alt={gallery.title}
                className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-105"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-background/90 via-background/20 to-transparent" />
              <div className="absolute top-3 right-3 flex gap-2">
                <button className="w-8 h-8 rounded-lg bg-background/80 backdrop-blur-sm flex items-center justify-center text-foreground hover:bg-background transition-colors">
                  <Eye className="w-4 h-4" />
                </button>
                <button className="w-8 h-8 rounded-lg bg-background/80 backdrop-blur-sm flex items-center justify-center text-foreground hover:bg-background transition-colors">
                  <Download className="w-4 h-4" />
                </button>
              </div>
              <div className="absolute bottom-3 left-3 right-3">
                <span className={`status-badge ${getStatusClass(gallery.status)}`}>
                  {getStatusIcon(gallery.status)}
                  <span className="ml-1">{gallery.status}</span>
                </span>
              </div>
            </div>
            <div className="p-4">
              <h3 className="font-semibold text-foreground truncate">{gallery.title}</h3>
              <p className="text-sm text-muted-foreground mt-1">{gallery.clientName}</p>
              <div className="flex items-center justify-between mt-3 pt-3 border-t border-border/50">
                <div className="flex items-center gap-1 text-sm text-muted-foreground">
                  <Image className="w-4 h-4" />
                  <span>{gallery.imageCount} photos</span>
                </div>
                <span className="text-sm text-muted-foreground">
                  {new Date(gallery.createdAt).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Booking } from '@/types';
import { Button } from '@/components/ui/button';
import { Plus, Calendar, MapPin, Clock, MoreHorizontal, Bell } from 'lucide-react';

interface BookingsViewProps {
  bookings: Booking[];
}

function getStatusClass(status: Booking['status']) {
  switch (status) {
    case 'pending': return 'status-pending';
    case 'confirmed': return 'status-confirmed';
    case 'completed': return 'status-delivered';
    case 'cancelled': return 'status-cancelled';
  }
}

export function BookingsView({ bookings }: BookingsViewProps) {
  const [filter, setFilter] = useState<'all' | Booking['status']>('all');

  const filteredBookings = filter === 'all'
    ? bookings
    : bookings.filter(b => b.status === filter);

  const sortedBookings = [...filteredBookings].sort(
    (a, b) => new Date(a.date).getTime() - new Date(b.date).getTime()
  );

  return (
    <div className="animate-fade-in">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Bookings</h2>
          <p className="text-muted-foreground mt-1">Schedule and manage photoshoots</p>
        </div>
        <Button>
          <Plus className="w-4 h-4" />
          New Booking
        </Button>
      </div>

      <div className="flex gap-2 mb-6">
        {(['all', 'pending', 'confirmed', 'completed', 'cancelled'] as const).map((status) => (
          <button
            key={status}
            onClick={() => setFilter(status)}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              filter === status
                ? 'bg-primary text-primary-foreground'
                : 'bg-secondary text-muted-foreground hover:text-foreground'
            }`}
          >
            {status.charAt(0).toUpperCase() + status.slice(1)}
          </button>
        ))}
      </div>

      <div className="space-y-4">
        {sortedBookings.map((booking, index) => (
          <div
            key={booking.id}
            className="bg-card border border-border/50 rounded-xl p-5 hover:border-primary/30 transition-all animate-slide-up"
            style={{ animationDelay: `${index * 50}ms` }}
          >
            <div className="flex items-start gap-4">
              {booking.clientAvatar ? (
                <img
                  src={booking.clientAvatar}
                  alt={booking.clientName}
                  className="w-12 h-12 rounded-full object-cover"
                />
              ) : (
                <div className="w-12 h-12 rounded-full bg-primary/20 flex items-center justify-center">
                  <span className="text-primary font-semibold">{booking.clientName[0]}</span>
                </div>
              )}

              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="font-semibold text-foreground">{booking.clientName}</h3>
                    <p className="text-primary font-medium text-sm">{booking.packageName}</p>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className={`status-badge ${getStatusClass(booking.status)}`}>
                      {booking.status}
                    </span>
                    <button className="w-8 h-8 rounded-lg hover:bg-secondary flex items-center justify-center text-muted-foreground hover:text-foreground transition-colors">
                      <MoreHorizontal className="w-4 h-4" />
                    </button>
                  </div>
                </div>

                <div className="flex flex-wrap items-center gap-4 mt-3">
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <Calendar className="w-4 h-4" />
                    <span>{new Date(booking.date).toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric', year: 'numeric' })}</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <Clock className="w-4 h-4" />
                    <span>{booking.time}</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <MapPin className="w-4 h-4" />
                    <span>{booking.location}</span>
                  </div>
                </div>

                {booking.notes && (
                  <p className="text-sm text-muted-foreground mt-3 p-3 bg-secondary/30 rounded-lg">
                    {booking.notes}
                  </p>
                )}
