@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 20 14% 4%;
    --foreground: 60 9% 98%;

    --card: 20 14% 7%;
    --card-foreground: 60 9% 98%;

    --popover: 20 14% 9%;
    --popover-foreground: 60 9% 98%;

    --primary: 38 92% 50%;
    --primary-foreground: 20 14% 4%;

    --secondary: 20 14% 14%;
    --secondary-foreground: 60 9% 98%;

    --muted: 20 14% 18%;
    --muted-foreground: 20 10% 55%;

    --accent: 38 92% 50%;
    --accent-foreground: 20 14% 4%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 60 9% 98%;

    --success: 142 76% 36%;
    --success-foreground: 60 9% 98%;

    --warning: 38 92% 50%;
    --warning-foreground: 20 14% 4%;

    --border: 20 14% 16%;
    --input: 20 14% 16%;
    --ring: 38 92% 50%;

    --radius: 0.75rem;

    --sidebar-background: 20 14% 6%;
    --sidebar-foreground: 60 9% 98%;
    --sidebar-primary: 38 92% 50%;
    --sidebar-primary-foreground: 20 14% 4%;
    --sidebar-accent: 20 14% 12%;
    --sidebar-accent-foreground: 60 9% 98%;
    --sidebar-border: 20 14% 14%;
    --sidebar-ring: 38 92% 50%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  .glass-card {
    @apply bg-card/60 backdrop-blur-xl border border-border/50 rounded-xl;
  }

  .stat-card {
    @apply bg-card border border-border/50 rounded-xl p-6 transition-all duration-300;
  }

  .stat-card:hover {
    @apply border-primary/30;
    box-shadow: 0 0 40px hsl(38 92% 50% / 0.1);
  }

  .glow-text {
    @apply text-primary;
    text-shadow: 0 0 20px hsl(38 92% 50% / 0.5);
  }

  .nav-item {
    @apply flex items-center gap-3 px-4 py-3 rounded-lg text-muted-foreground transition-all duration-200;
  }

  .nav-item:hover {
    @apply bg-secondary text-foreground;
  }

  .nav-item.active {
    @apply bg-primary/10 text-primary border-l-2 border-primary;
  }

  .status-badge {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
  }

  .status-pending {
    @apply bg-warning/20 text-warning;
  }

  .status-confirmed {
    @apply bg-success/20 text-success;
  }

  .status-delivered {
    @apply bg-primary/20 text-primary;
  }

  .status-cancelled {
    @apply bg-destructive/20 text-destructive;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-out forwards;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out forwards;
  }

  .animate-slide-in {
    animation: slideIn 0.3s ease-out forwards;
  }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideIn {
    from {
      opacity: 0;
      transform: translateX(-10px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }
}

